function changeLocation(val, amd64) {
  for (const a in val) {
    if (val[a].name.toLowerCase() === 'location') {
      if (amd64)
        val[a].value = val[a].value.split('?')[0].replace('BlueStacksMicroInstaller', 'FullInstaller/x64/BlueStacksFullInstaller').replace('native', 'amd64_native')
      else
        val[a].value = val[a].value.split('?')[0].replace('BlueStacksMicroInstaller', 'FullInstaller/x86/BlueStacksFullInstaller').replace('native', 'x86_native')
      break
    }
  }
}
